    List<CategoryProducts> categoryProducts;

    protected override void OnInitialized()
    {
        base.OnInitialized();
        GetProducts();
    }

    void GetProducts()
    {
        categoryProducts = context.Products
            .GroupBy(p => p.Category)
            .Select(p => new CategoryProducts
            {
                Category = p.Key,
                ProductsCount = p.Count()
            }).ToList();

    }

